async function generateTailwindCss(html: string) {
  const { css } = await postcss([
    tailwindcss({
      content: [{ raw:html }],
      theme: {
        extend: {},
      },
      plugins: [],
      corePlugins: {
        preflight: false,
      },
    }), 
  ])
    .process('@tailwind utilities;', { from:undefined })

  return css
}


export async function generatePage(page: Page) {
  const { elements, fonts } = page
  const app = createSSRApp({
    components: components,
    render(){
      return h('div', { class: 'w-full min-h-screen bg-gradient-to-r from-cyan-500 to-blue-500' }, [
        h('div', { class: 'max-w-sm mx-auto pt-12' }, [
          renderer({ elements })
        ])
      ])
    }
  })

  const html  =  (await renderToString(app)).replaceAll('&#39;', '\'')

  const tailwind = await generateTailwindCss(html)

  const { code:css } = transform({
    filename: 'style.css',
    code: Buffer.from(tailwind),
    minify: true,
  })


  return (`
  <!doctype html>
    <html lang="en">
      <head>
        <meta charset="UTF-8" />
        <link rel="icon" type="image/svg+xml" href="/vite.svg" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>Vite + Vue + TS</title>
        <style></style>
        <style>${preflight}${css}</style>
        <link rel="preconnect" href="https://fonts.googleapis.com">
        <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>  
        ${fonts?.map((font) => `
        <link href="https://fonts.googleapis.com/css2?family=${font.family.replaceAll('_', '+').replaceAll('\'','')}:wght@${font.weight.join(';')}&display=swap" rel="stylesheet">
        `).join('')}
      </head>
      <body>
        <div id="app">
        ${html}
        </div>
      </body>
    </html>
  `)
}

